    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
       //boolean variable to ensure function was succesful
        boolean success = AddItem();
        if (success) {
            JOptionPane.showMessageDialog(this, "Successfully added item to database");
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(this, "Error: Failed to add to database", "Error: Format error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnAddActionPerformed

