    // Add equipment item method
    public boolean AddItem() {
       // Create database interface object
       DataIO data = new DataIO();
       // Create new equipment item object
       Item item = new Item();
       //if ID was not selected from table, set from user input
       if (!txfID.getText().equals("")) {
           
           try {
               // Parse textfield to Integer wrapper and set the item id
               item.setItemID(Integer.parseInt(txfID.getText()));
           } catch (Exception ex) {
               // Invalid entry - NumberFormatException
               JOptionPane.showMessageDialog(this, "Error: Invalid ID, must be an integer", "Error: Invalid format", JOptionPane.ERROR_MESSAGE);
               return false;
           }
       }
       
       // Set the equipment item description
       item.setDescription(txfDescription.getText());
       // Set the equipment item name
       item.setName(txfName.getText());
       
       try {
           // Parse textfield for quantity and check if less than 1
           if (Integer.parseInt(txfQuantity.getText()) < 1) {
               // Prompt user with JOption dialog window
                JOptionPane.showMessageDialog(this, "Error: Quantity must be greater than 0", "Error: Invalid quantity", JOptionPane.ERROR_MESSAGE);
                return false;
            }
           
           // Parse textfield to Integer wrapper and set the item quantity
           item.setQuantity(Integer.parseInt(txfQuantity.getText()));
           
       } catch (Exception ex) {
           JOptionPane.showMessageDialog(this, "Error: Invalid quantity, must be an integer", "Error: Invalid format", JOptionPane.ERROR_MESSAGE);
           return false;
       }
       
       try {
           // Add equipment item into the database using DataIO method call
            data.addItem(item);
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(this, "Error: Could not load inventory", "Error: Network connection", JOptionPane.ERROR_MESSAGE);
            return false;
        } catch (ClassNotFoundException cex) {
            JOptionPane.showMessageDialog(this, "Error: Could not load inventory", "Error: SQL Driver", JOptionPane.ERROR_MESSAGE);
            return false;
        }
       
       return true;
    }
